'use strict';

const elasticsearch = require('elasticsearch');
const getIndex = require('./getIndex');

//const host = process.env.HUBOT_ELASTICSEARCH_LOGGER_HOST || '10.224.90.33';
const host = process.env.ELASTIC_SEARCH_IP;
const port = process.env.ELASTIC_SEARCH_PORT;
const rawIndex = 'hubot-today';
//const port = process.env.HUBOT_ELASTICSEARCH_LOGGER_PORT || 9200;
//const rawIndex = process.env.HUBOT_ELASTICSEARCH_LOGGER_INDEX || 'hubot-New';

const index = getIndex(rawIndex);
const type = 'message';
const id = process.env.HUBOT_NAME + '_log';

const client = new elasticsearch.Client({
  host: `${host}:${port}`
});

module.exports.logs = (data, cb) => {
//console.log(data);
  client.index({
    index,
    type,
    body: data
  },cb);
};

module.exports.log = (data, cb) => {
//console.log(data);
  client.index({
    index,
    type,
    id,
    body: data
  },cb);
};